import React, { useState, useEffect } from 'react';
import './App.css';

// Configuración de la API
const API_BASE_URL = 'http://localhost:8000';

function App() {
  const [activeTab, setActiveTab] = useState('dashboard');
  const [data, setData] = useState({
    insumos: [],
    moldes: [],
    colores: [],
    stats: {}
  });
  const [loading, setLoading] = useState(false);
  const [notification, setNotification] = useState('');

  // Cargar datos iniciales
  useEffect(() => {
    loadAllData();
  }, []);

  const loadAllData = async () => {
    setLoading(true);
    try {
      const [insumosRes, moldesRes, coloresRes, statsRes] = await Promise.all([
        fetch(`${API_BASE_URL}/insumos`),
        fetch(`${API_BASE_URL}/moldes`),
        fetch(`${API_BASE_URL}/colores`),
        fetch(`${API_BASE_URL}/resumen-completo`)
      ]);

      const insumos = await insumosRes.json();
      const moldes = await moldesRes.json();
      const colores = await coloresRes.json();
      const stats = await statsRes.json();

      setData({
        insumos: insumos.insumos || [],
        moldes: moldes.moldes || [],
        colores: colores.colores || [],
        stats
      });

      showNotification('Datos cargados correctamente ✅');
    } catch (error) {
      console.error('Error cargando datos:', error);
      showNotification('Error cargando datos ❌');
    } finally {
      setLoading(false);
    }
  };

  const showNotification = (message) => {
    setNotification(message);
    setTimeout(() => setNotification(''), 3000);
  };

  return (
    <div className="app">
      {/* Header */}
      <header className="header">
        <div className="container">
          <h1>🕯️ Sistema Vel Arte - Gestión Completa</h1>
          <button onClick={loadAllData} className="btn-refresh">
            🔄 Actualizar
          </button>
        </div>
      </header>

      {/* Loading bar */}
      {loading && <div className="loading-bar"></div>}

      {/* Notification */}
      {notification && (
        <div className="notification">
          {notification}
        </div>
      )}

      {/* Navigation */}
      <nav className="nav-tabs">
        <div className="container">
          <button 
            className={`tab ${activeTab === 'dashboard' ? 'active' : ''}`}
            onClick={() => setActiveTab('dashboard')}
          >
            📊 Dashboard
          </button>
          <button 
            className={`tab ${activeTab === 'insumos' ? 'active' : ''}`}
            onClick={() => setActiveTab('insumos')}
          >
            📦 Insumos ({data.insumos.length})
          </button>
          <button 
            className={`tab ${activeTab === 'moldes' ? 'active' : ''}`}
            onClick={() => setActiveTab('moldes')}
          >
            🏺 Moldes ({data.moldes.length})
          </button>
          <button 
            className={`tab ${activeTab === 'colores' ? 'active' : ''}`}
            onClick={() => setActiveTab('colores')}
          >
            🎨 Colores ({data.colores.length})
          </button>
          <button 
            className={`tab ${activeTab === 'calculadora' ? 'active' : ''}`}
            onClick={() => setActiveTab('calculadora')}
          >
            🧮 Calculadora
          </button>
        </div>
      </nav>

      {/* Content */}
      <main className="main-content">
        <div className="container">
          {activeTab === 'dashboard' && <Dashboard data={data} />}
          {activeTab === 'insumos' && <Insumos data={data} onRefresh={loadAllData} showNotification={showNotification} />}
          {activeTab === 'moldes' && <Moldes data={data} onRefresh={loadAllData} showNotification={showNotification} />}
          {activeTab === 'colores' && <Colores data={data} onRefresh={loadAllData} showNotification={showNotification} />}
          {activeTab === 'calculadora' && <Calculadora data={data} showNotification={showNotification} />}
        </div>
      </main>
    </div>
  );
}

