// Calculadora Component
function Calculadora({ data, showNotification }) {
  const [moldeSeleccionado, setMoldeSeleccionado] = useState('');
  const [insumosSeleccionados, setInsumosSeleccionados] = useState({});

  const calcularCosto = () => {
    showNotification('Calculadora de costos próximamente - Conectando con backend 🧮');
  };

  return (
    <div className="calculadora">
      <h2>🧮 Calculadora de Costos Avanzada</h2>

      <div className="calculator-grid">
        <div className="calculator-inputs">
          <div className="input-group">
            <label>🏺 Seleccionar Molde:</label>
            <select
              value={moldeSeleccionado}
              onChange={(e) => setMoldeSeleccionado(e.target.value)}
              className="molde-select"
            >
              <option value="">Selecciona un molde...</option>
              {data.moldes.map(molde => (
                <option key={molde.codigo} value={molde.codigo}>
                  {molde.codigo} - {molde.descripcion}
                </option>
              ))}
            </select>
          </div>

          <div className="insumos-section">
            <h3>📦 Seleccionar Insumos</h3>
            {data.insumos.slice(0, 5).map(insumo => (
              <div key={insumo.codigo} className="insumo-input">
                <div className="insumo-header">
                  <strong>{insumo.descripcion}</strong>
                  <small>{insumo.codigo}</small>
                </div>
                <input
                  type="number"
                  placeholder={`Cantidad (${insumo.unidad_medida})`}
                  onChange={(e) => {
                    setInsumosSeleccionados({
                      ...insumosSeleccionados,
                      [insumo.codigo]: e.target.value
                    });
                  }}
                />
              </div>
            ))}
          </div>
        </div>

        <div className="calculator-result">
          <div className="result-card">
            <h3>💰 Resultado del Cálculo</h3>

            <div className="result-display">
              <div className="result-price">
                <span className="currency">$</span>
                <span className="amount">0</span>
              </div>
              <p>Precio final sugerido</p>
            </div>

            <button
              className="btn-calculate"
              onClick={calcularCosto}
              disabled={!moldeSeleccionado}
            >
              🧮 Calcular Costo
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

// Función auxiliar para colores
function getColorByName(nombre) {
  const colores = {
    'Ultramarine': '#120A8F',
    'Fluorescent blue': '#0080FF',
    'Blue': '#0000FF',
    'Cerulean blue': '#2A52BE'
  };
  return colores[nombre] || '#' + Math.floor(Math.random()*16777215).toString(16);
}

export default App;
